INSERT INTO "subcategory" ("id", "title", "parent_category", "lang") VALUES ('ad49e3ca-615d-4da2-95cf-ea15073d5a1b', 'Menstruations et règles', '0fa1b41d-3d37-4a66-8dbe-7717514eb8c6', 'fr'), ('cb8fa267-95ba-4077-91c2-2f71150bf24e', 'Irrégularité et absence de règles', '0fa1b41d-3d37-4a66-8dbe-7717514eb8c6', 'fr'), ('f3dfea92-d247-421c-aa52-4b2e4c70125e', 'Sang, flux et pertes', '0fa1b41d-3d37-4a66-8dbe-7717514eb8c6', 'fr'), ('03c96109-09c3-471a-8bc9-cdc535459fb4', 'Hygiène', '7500a772-99fe-44ea-bde2-793db383bd5f', 'fr'), ('3859ad25-83eb-41f5-8792-ef9cd237fc04', 'Protections hygiéniques', '7500a772-99fe-44ea-bde2-793db383bd5f', 'fr'), ('0521b24f-5617-4ee5-ac9a-f8e7296793bf', 'Douleurs et SPM', '7500a772-99fe-44ea-bde2-793db383bd5f', 'fr'), ('a7c8b863-8cc3-45ab-9563-7c148151a82c', 'Nutrition et alimentation', 'aeea772a-1c16-4aee-90a0-4a0701211318', 'fr'), ('6b559cff-21e4-4b07-b412-2e9fc55ef23b', 'Exercice', 'aeea772a-1c16-4aee-90a0-4a0701211318', 'fr'), ('364941b5-573c-42a0-a355-6c6493c507ea', 'Corps et poids', 'aeea772a-1c16-4aee-90a0-4a0701211318', 'fr'), ('a8b5b171-f785-49de-809a-57c501eeb49d', 'Puberté', '7eddca67-ec89-4573-b22c-633e586de897', 'fr'), ('ebb706bd-7e02-4de8-b996-93dafb39ab12', 'Sexualité', '4f9aa8bd-00c9-4ee9-ad42-f495cb5ecd7a', 'fr'), ('58ead0fc-1f1e-4658-acbb-88d045126e40', 'Fertilité', '4f9aa8bd-00c9-4ee9-ad42-f495cb5ecd7a', 'fr'), ('b9a70141-1668-43cd-8db5-55a8f0598cad', 'Grossesse', '4f9aa8bd-00c9-4ee9-ad42-f495cb5ecd7a', 'fr'), ('490c121d-0c30-4c60-a522-453529e6bbc5', 'Garçons et hommes', '8e0d3c59-9ff1-42f1-b6ce-a674b3390a87', 'fr'), ('ae77614b-1407-4be5-8f44-2bf1221c8621', 'Genre', '8e0d3c59-9ff1-42f1-b6ce-a674b3390a87', 'fr'), ('8e2a7c47-844d-4337-9880-8cab7786436b', 'Relations', '8e0d3c59-9ff1-42f1-b6ce-a674b3390a87', 'fr'), ('17f584c8-ac74-4958-aba2-6016ae5a842a', 'Peur', '922beecc-8ecb-4525-a596-bed566c55a78', 'fr'), ('5dbd7b92-52a3-420e-a692-951ea78d53b7', 'Honte', '922beecc-8ecb-4525-a596-bed566c55a78', 'fr'), ('6b0ce015-2648-4358-a8be-